import React from "react";
import TextField from "@material-ui/core/TextField";

const Input = ({ label, name, type = "text", value, onChange, error }) => {
  return (
    <div className="form-group">
      <TextField
        style={{ width: "100%" }}
        required
        id={name}
        name={name}
        label={label}
        type={type}
        defaultValue={value}
        onChange={onChange}
        variant="outlined"
      />
      {error && (
        <TextField
          id="outlined-helperText"
          label="Helper text"
          defaultValue="Default Value"
          helperText={error}
          variant="outlined"
        />
      )}
    </div>
    // <div className="form-group">
