import React, { Component } from "react";
import { getPosts, deletePost } from "../../../services/postsService";

class SharePoint extends Component {
  state = {
    posts: [],
  };

  async componentDidMount() {
    const { data: posts } = await getPosts();

    this.setState({ posts });
  }

  handleDelete = async (post) => {
    const originalPosts = this.state.posts;
    const posts = originalPosts.filter((p) => p.id !== post.id);
    this.setState({ posts });

    try {
      await deletePost(post.id);
    } catch (ex) {
      if (ex.response && ex.response.status === 404) console.log("x");
      // toast.error("This movie has already been deleted.");

      this.setState({ posts: originalPosts });
    }
  };

  handleRoute = (param) => {
    const routePath = "/rules-extraction/ingestion/sharepoint-form/" + param;
    this.props.history.push(routePath);
  };

  render() {
    return (
      <div className="sharepoint-repo">
        <div className="wrapper">
          <button
            className="btn btn-outline-primary"
            onClick={() => this.handleRoute("new")}
          >
            Add Post
          </button>
          <div className="table-container">
            <div className="table-wrapper table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>UserId</th>
                    <th>Title</th>
                    <th>Body</th>
                    <th></th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.posts.map((post) => (
                    <tr key={post.id}>
                      <td>{post?.userId}</td>
                      <td>{post?.title}</td>
                      <td>{post?.body}</td>
                      <td>
                        <button className="btn btn-sm btn-outline-primary">
                          <i className="fa fa-pencil"></i>
                        </button>
                      </td>
                      <td>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => this.handleDelete(post)}
                        >
                          <i className="fa fa-trash-o"></i>
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default SharePoint;
