import React from "react";

// This is the table constant/settings which needed to render table elements
export const tableConstants = (handleEdit) => {
  return [
    {
      title: "ID",
      render: (rowData) => {
        return <span>{rowData._id}</span>;
      },
    },
    {
      title: "Name",
      render: (rowData) => {
        return <span>{rowData.name}</span>;
      },
    },
    {
      title: "Company Name",
      render: (rowData) => {
        return <span>{rowData.companyName}</span>;
      },
    },
    {
      title: "Email",
      render: (rowData) => {
        return <span>{rowData.email}</span>;
      },
    },
    {
      title: "Gender",
      render: (rowData) => {
        return <span>{rowData.gender}</span>;
      },
    },
    {
      title: "City",
      render: (rowData) => {
        return <span>{rowData.city}</span>;
      },
    },
    {
      title: "Country",
      render: (rowData) => {
        return <span>{rowData.country}</span>;
      },
    },
    {
      title: "Action",
      render: (rowData) => {
        return (
          // <button
          //   className="btn btn-warning"
          //   onClick={() => handleEdit(rowData)}
          // >
          //   Edit
          // </button>
          <div className="btn-group-sm">
            <button
              className="btn btn-sm btn-outline-primary mr-1"
              onClick={() => handleEdit(rowData)}
            >
              <i className="fa fa-pencil"></i>
            </button>
            <button className="btn btn-sm btn-outline-danger mr-1">
              <i className="fa fa-trash-o"></i>
            </button>
          </div>
        );
      },
    },
  ];
};
