import React from "react";
import { TextField } from "@material-ui/core";

const TextArea = ({ label, name, type = "text", value, onChange, error }) => {
  return (
    <div className="form-group">
      <TextField
        multiline
        variant="outlined"
        id={name}
        name={name}
        className="form-control"
        type={type}
        defaultValue={value}
        onChange={onChange}
        label={label}
        rows={5}
      />
      {/* <label htmlFor={name}>{label}</label>
      <textarea
        id={name}
        name={name}
        className="form-control"
        type={type}
        value={value}
        onChange={onChange}
        placeholder={label}
        rows="5"
      ></textarea> */}
      {error && <small className="alert alert-danger">{error}</small>}
    </div>
  );
};

export default TextArea;
