import React, { Component } from "react";

import AppHeader from "./components/appHeader";
import "./App.scss";
import { Switch, Route } from "react-router-dom";
import AppContainer from "./components/appContainer";
import ListGroup from "./common/listGroup";

class App extends Component {
  state = {
    header: {
      brandName: "HINT",
    },
    toggleSidenav: true,
  };

  toggleSidenav = () => {
    const toggle = this.state.toggleSidenav;
    this.setState({ toggleSidenav: !toggle });
  };

  renderSidenav() {
    if (this.state.toggleSidenav) {
      return (
        <div className="sidenav-wrapper">
          <div className="sidenav">
            <ListGroup />
          </div>
        </div>
      );
    }
  }

  render() {
    return (
      <div className="App">
        {this.renderSidenav}
        <header className="App-header">
          <AppHeader header={this.state.header} />
        </header>
        <section className="main-router-container">
          <Switch>
            <Route to="/" exact component={AppContainer} />
          </Switch>
        </section>
      </div>
    );
  }
}

export default App;
