import React from "react";
import { useLocation, Link } from "react-router-dom";

import Breadcrumbs from "@material-ui/core/Breadcrumbs";
// import Link from '@material-ui/core/Link';

import "./breadcrumb.scss";

const Breadcrumb = () => {
  const locations = useLocation().pathname.split("/");
  const getClasses = (breadcrumb) => {
    if (breadcrumb.last) {
      return "breadcrumb-item last";
    }
    return "breadcrumb-item";
  };

  const breadcrumbs = [
    {
      _id: "bdcb0",
      name: "home",
      link: "/",
      last: false,
    },
  ];
  locations.map((location, i) => {
    const breadcrumb = {
      _id: "",
      name: "",
      link: "",
      last: false,
    };
    if (i !== 0) {
      breadcrumb._id = `bdcb${i}`;
      breadcrumb.name = location;
      breadcrumb.link = breadcrumbs[i - 1].link + location + "/";
      breadcrumbs.push(breadcrumb);
    }

    if (i === locations.length - 1) {
      breadcrumb.last = true;
    }
    return null;
  });

  return (
    // <nav className="breadcrumb-wrapper" aria-label="breadcrumb">
    //   <ol className="breadcrumb">
    //     {breadcrumbs.map((b) => (
    //       <li key={b._id} className={getClasses(b)}>
    //         <Link to={b.link}>{b.name}</Link>
    //       </li>
    //     ))}
    //   </ol>
    // </nav>
    <Breadcrumbs aria-label="breadcrumb" className="breadcrumb-wrapper">
      {breadcrumbs.map((b) => (
        <Link color="inherit" to={b.link}>
          {b.name}
        </Link>
      ))}
      {/* <Link color="inherit" href="/" onClick={handleClick}>
      Material-UI
    </Link>
    <Link color="inherit" href="/getting-started/installation/" onClick={handleClick}>
      Core
    </Link>
    <Link
      color="textPrimary"
      href="/components/breadcrumbs/"
      onClick={handleClick}
      aria-current="page"
    >
      Breadcrumb
    </Link> */}
    </Breadcrumbs>
  );
};

export default Breadcrumb;
