import React from "react";
import { NavLink, useLocation } from "react-router-dom";

import "./breadcrumb.scss";

const Breadcrumb = () => {
  const locations = useLocation().pathname.split("/");
  const breadcrumbs = [
    {
      _id: "bdcb0",
      name: "home",
      link: "/",
      last: false,
    },
  ];
  locations.map((location, i) => {
    const breadcrumb = {
      _id: "",
      name: "",
      link: "",
      last: false,
    };
    if (i !== 0) {
      breadcrumb._id = `bdcb${i}`;
      breadcrumb.name = location;
      breadcrumb.link = breadcrumbs[i - 1].link + location + "/";
      console.log(breadcrumb);
      breadcrumbs.push(breadcrumb);
    }

    if (i === locations.length - 1) {
      breadcrumb.last = true;
    }
  });
  console.log(breadcrumbs);

  return (
    <nav className="breadcrumb-wrapper" aria-label="breadcrumb">
      <ol className="breadcrumb">
        {breadcrumbs.map((b) => (
          // <li key={b._id} className="breadcrumb-item">
          //   <NavLink to={b.path}>{b.title}</NavLink>
          // </li>
          <li key={b._id} className="breadcrumb-item">
            <NavLink to={b.link}>{b.name}</NavLink>
          </li>
        ))}
      </ol>
    </nav>
  );
};

export default Breadcrumb;
